  var redIcon = L.divIcon({
    className : 'div-red-icon',
    html: '<span style="color: white; background-color: red; font-size: 14px;">' + score + '<img width="16px" src="/vaczones/images/google_icon.png"/></span>'
  })

  var emptyIcon = L.divIcon({
